class Tester:
    
    def assertEqual(self, result, expected):
        if result != expected:
            raise AssertionError('error, expected %s, got %s' 
                %(expected, result))

    def assertRaises(self, exc_type, func, *args, **kw):
        try:
            x = func(*args,**kw)
        except Exception as exc:
            if not isinstance(exc, exc_type):
                raise AssertionError('%s %s %s should raise %s, raised %s'
                    %(func, str(args), str(kw), exc_type, exc))
            return
        raise AssertionError('%s %s %s should raise %s, returned %s'
            %(func, str(args), str(kw), exc_type, x))